// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./ISubscriptionManager.sol";
import "./ISignatureManager.sol";
import "./ITokenManager.sol";
import "./IAccessManager.sol";

contract AIAgentWalletWithSubscriptions is ERC721, EIP712, ReentrancyGuard {
    ISubscriptionManager public subscriptionManager;
    ISignatureManager public signatureManager;
    ITokenManager public tokenManager;
    IAccessManager public accessManager;

    uint256 public cooldownPeriod = 1 hours;
    uint256 public platformFeePercent = 5;
    uint256 public mintFee = 0.01 ether;
    address public feeRecipient;
    uint256 private nextTokenId;
    // Events for logging
    event SpendETH(
        address indexed user,
        address indexed recipient,
        uint256 amount,
        uint256 fee
    );
    event SpendToken(
        address indexed user,
        address indexed token,
        address indexed recipient,
        uint256 amount,
        uint256 fee
    );

    mapping(address => uint256) private lastOperationTime;

    event PlatformConfigurationUpdated(
        uint256 oldFeePercent,
        uint256 newFeePercent,
        address oldRecipient,
        address newRecipient
    );

    modifier onlyAIAgent() {
        require(msg.sender == accessManager.aiAgent(), "Only AI Agent allowed");
        _;
    }

    modifier whenNotPaused() {
        require(!accessManager.isPaused(), "Contract is paused");
        _;
    }

    modifier cooldown(address user) {
        require(
            block.timestamp >= lastOperationTime[user] + cooldownPeriod,
            "Operation on cooldown"
        );
        _;
        lastOperationTime[user] = block.timestamp;
    }

    constructor(
        string memory name,
        string memory symbol,
        string memory version,
        address subscriptionManagerAddress,
        address signatureManagerAddress,
        address tokenManagerAddress,
        address accessManagerAddress,
        address _feeRecipient
    ) ERC721(name, symbol) EIP712(name, version) {
        subscriptionManager = ISubscriptionManager(subscriptionManagerAddress);
        signatureManager = ISignatureManager(signatureManagerAddress);
        tokenManager = ITokenManager(tokenManagerAddress);
        accessManager = IAccessManager(accessManagerAddress);
        require(
            _feeRecipient != address(0),
            "Fee recipient cannot be zero address"
        );
        feeRecipient = _feeRecipient;
    }

    // ====== Wallet Functionality ======

    function mintNFT(
        address recipient
    ) external payable onlyAIAgent nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(msg.value == mintFee, "Incorrect mint fee");
        uint256 tokenId = nextTokenId++;
        _mint(recipient, tokenId);
        payable(feeRecipient).transfer(msg.value);
    }

    function depositETH() external payable whenNotPaused {
        tokenManager.depositETH{value: msg.value}();
    }

    function depositToken(
        address token,
        uint256 amount
    ) external whenNotPaused {
        tokenManager.depositToken(token, amount);
    }

    function spendETH(
        address user,
        address recipient,
        uint256 amount
    ) external onlyAIAgent whenNotPaused nonReentrant {
        uint256 fee = (amount * platformFeePercent) / 100;
        uint256 netAmount = amount - fee;

        require(
            tokenManager.ethBalances(user) >= amount,
            "Insufficient ETH balance"
        );

        // Deduct total amount and transfer
        tokenManager.withdrawETH(user,amount);
        payable(feeRecipient).transfer(fee);
        payable(recipient).transfer(netAmount);

        emit SpendETH(user, recipient, netAmount, fee);
    }

    function spendToken(
        address user,
        address token,
        address recipient,
        uint256 amount
    ) external onlyAIAgent whenNotPaused nonReentrant {
        uint256 fee = (amount * platformFeePercent) / 100;
        uint256 netAmount = amount - fee;

        require(
            tokenManager.tokenBalances(user, token) >= amount,
            "Insufficient token balance"
        );

        // Deduct total amount and transfer
        tokenManager.withdrawToken(user,token, amount);
        IERC20(token).transfer(feeRecipient, fee);
        IERC20(token).transfer(recipient, netAmount);

        emit SpendToken(user, token, recipient, netAmount, fee);
    }

    function emergencyWithdraw() external whenNotPaused nonReentrant {
        uint256 userEthBalance = tokenManager.ethBalances(msg.sender);
        if (userEthBalance > 0) {
            tokenManager.withdrawETH(msg.sender,userEthBalance);
        }

        address[] memory tokens = tokenManager.getUserTokenList(msg.sender);
        for (uint256 i = 0; i < tokens.length; i++) {
            uint256 tokenBalance = tokenManager.tokenBalances(
                msg.sender,
                tokens[i]
            );
            if (tokenBalance > 0) {
                tokenManager.withdrawToken(msg.sender,tokens[i], tokenBalance);
            }
        }
    }

    function executeTransaction(
        address user,
        address token,
        address recipient,
        uint256 amount,
        bytes32 digest,
        bytes memory signature
    ) external onlyAIAgent whenNotPaused cooldown(user) nonReentrant {
        require(
            signatureManager.verifySignature(user, digest, signature),
            "Invalid signature"
        );

        uint256 fee = (amount * platformFeePercent) / 100;
        uint256 netAmount = amount - fee;

        if (token == address(0)) {
            require(
                tokenManager.ethBalances(user) >= amount,
                "Insufficient ETH balance"
            );
            tokenManager.withdrawETH(address(this),amount);
            payable(feeRecipient).transfer(fee);
            payable(recipient).transfer(netAmount);
        } else {
            require(
                tokenManager.tokenBalances(user, token) >= amount,
                "Insufficient token balance"
            );
            tokenManager.withdrawToken(address(this),token, amount);
            IERC20(token).transfer(feeRecipient, fee);
            IERC20(token).transfer(recipient, netAmount);
        }

        signatureManager.incrementNonce(user);
    }

    // ====== Admin Functions ======

    function updatePlatformConfiguration(
        uint256 newFeePercent,
        address newRecipient
    ) external {
        require(
            msg.sender == accessManager.owner(),
            "Only owner can update configuration"
        );
        require(newFeePercent <= 100, "Fee percent cannot exceed 100");
        require(newRecipient != address(0), "Invalid recipient address");

        uint256 oldFeePercent = platformFeePercent;
        address oldRecipient = feeRecipient;

        platformFeePercent = newFeePercent;
        feeRecipient = newRecipient;

        emit PlatformConfigurationUpdated(
            oldFeePercent,
            newFeePercent,
            oldRecipient,
            newRecipient
        );
    }

    receive() external payable whenNotPaused {
        tokenManager.depositETH{value: msg.value}();
    }

    fallback() external payable {
        revert("Fallback not supported");
    }
}

