// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SignatureManager is Ownable {
    using ECDSA for bytes32;

    mapping(address => uint256) private userNonces;
    address public aiAgent;

    event NonceUpdated(address indexed user, uint256 newNonce);
    event AIAgentUpdated(address indexed oldAgent, address indexed newAgent);

    modifier onlyAIAgent() {
        require(msg.sender == aiAgent, "Only AI Agent allowed");
        _;
    }

    constructor(address _aiAgent) Ownable(msg.sender) {
        require(_aiAgent != address(0), "AI Agent address cannot be zero");
        aiAgent = _aiAgent;
    }

    function setAIAgent(address newAgent) external onlyOwner {
        require(newAgent != address(0), "Invalid AI Agent address");
        emit AIAgentUpdated(aiAgent, newAgent);
        aiAgent = newAgent;
    }

    function verifySignature(
        address user,
        bytes32 digest,
        bytes memory signature
    ) public pure returns (bool) {
        return digest.recover(signature) == user;
    }

    function getUserNonce(address user) public view returns (uint256) {
        return userNonces[user];
    }

    function incrementNonce(address user) external onlyAIAgent {
        userNonces[user] += 1;
        emit NonceUpdated(user, userNonces[user]);
    }
}

