// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

contract SubscriptionManager is Ownable {
    enum Plan {
        Basic,
        Pro,
        Enterprise
    }

    uint256 public basicPrice;
    uint256 public proPrice;
    uint256 public enterprisePrice;
    uint256 public subscriptionDuration;

    mapping(address => Plan) public subscriberPlans;
    mapping(address => uint256) public subscriberExpiry;

    event Subscribed(address indexed user, Plan plan, uint256 expiry);
    event PlanUpgraded(
        address indexed user,
        Plan oldPlan,
        Plan newPlan,
        uint256 expiry
    );
    event Unsubscribed(address indexed user);
    event PriceUpdated(
        uint256 basicPrice,
        uint256 proPrice,
        uint256 enterprisePrice
    );

    constructor(
        uint256 _basicPrice,
        uint256 _proPrice,
        uint256 _enterprisePrice,
        uint256 _subscriptionDuration
    ) Ownable(msg.sender) {
        basicPrice = _basicPrice;
        proPrice = _proPrice;
        enterprisePrice = _enterprisePrice;
        subscriptionDuration = _subscriptionDuration;
    }

    modifier isSubscribed(address user) {
        require(
            subscriberExpiry[user] > block.timestamp,
            "Subscription expired or not active"
        );
        _;
    }

    function subscribe(Plan _plan) external payable {
        uint256 price = getPlanPrice(_plan);
        require(msg.value == price, "Incorrect subscription fee");

        uint256 newExpiry = block.timestamp + subscriptionDuration;

        if (subscriberExpiry[msg.sender] < block.timestamp) {
            subscriberPlans[msg.sender] = _plan;
        } else {
            require(
                newExpiry > subscriberExpiry[msg.sender],
                "Expiry overflow detected"
            );
        }

        subscriberExpiry[msg.sender] = newExpiry;
        emit Subscribed(msg.sender, _plan, newExpiry);
    }

    function upgradePlan(
        Plan _newPlan
    ) external payable isSubscribed(msg.sender) {
        Plan currentPlan = subscriberPlans[msg.sender];
        require(_newPlan > currentPlan, "Can only upgrade to a higher plan");

        uint256 additionalCost = getPlanPrice(_newPlan) -
            getPlanPrice(currentPlan);
        require(
            msg.value == additionalCost,
            "Incorrect value sent for upgrade"
        );

        subscriberPlans[msg.sender] = _newPlan;
        emit PlanUpgraded(
            msg.sender,
            currentPlan,
            _newPlan,
            subscriberExpiry[msg.sender]
        );
    }

    function unsubscribe() external isSubscribed(msg.sender) {
        delete subscriberPlans[msg.sender];
        delete subscriberExpiry[msg.sender];
        emit Unsubscribed(msg.sender);
    }

    function getPlanPrice(Plan _plan) public view returns (uint256) {
        if (_plan == Plan.Basic) return basicPrice;
        if (_plan == Plan.Pro) return proPrice;
        if (_plan == Plan.Enterprise) return enterprisePrice;
        revert("Invalid plan");
    }

    function setPrices(
        uint256 _basicPrice,
        uint256 _proPrice,
        uint256 _enterprisePrice
    ) external onlyOwner {
        basicPrice = _basicPrice;
        proPrice = _proPrice;
        enterprisePrice = _enterprisePrice;
        emit PriceUpdated(_basicPrice, _proPrice, _enterprisePrice);
    }

    function setSubscriptionDuration(uint256 _duration) external onlyOwner {
        subscriptionDuration = _duration;
    }
}

contracts/TokenManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract TokenManager is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    mapping(address => uint256) public ethBalances;
    mapping(address => mapping(address => uint256)) public tokenBalances;
    mapping(address => address[]) private userTokenList;

    uint256 public maxTokenListSize = 50;
    address public authorizedSpender; // Authorized contract to spend on behalf of users

    event DepositETH(address indexed user, uint256 amount);
    event DepositToken(
        address indexed user,
        address indexed token,
        uint256 amount
    );
    event WithdrawETH(address indexed user, uint256 amount);
    event WithdrawToken(
        address indexed user,
        address indexed token,
        uint256 amount
    );
    event SpenderAuthorized(address indexed newSpender);

    modifier onlyAuthorizedSpender() {
        require(msg.sender == authorizedSpender, "Not authorized to spend");
        _;
    }

    constructor() Ownable(msg.sender) ReentrancyGuard() {}

    function authorizeSpender(address newSpender) external onlyOwner {
        require(newSpender != address(0), "Invalid spender address");
        authorizedSpender = newSpender;
        emit SpenderAuthorized(newSpender);
    }

    function depositETH() external payable {
        ethBalances[msg.sender] += msg.value;
        emit DepositETH(msg.sender, msg.value);
    }

    function depositToken(address token, uint256 amount) external {
        require(token != address(0), "Invalid token address");
        require(amount > 0, "Amount must be greater than zero");

        uint256 allowance = IERC20(token).allowance(msg.sender, address(this));
        require(allowance >= amount, "Insufficient token allowance");

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        if (tokenBalances[msg.sender][token] == 0) {
            require(
                userTokenList[msg.sender].length < maxTokenListSize,
                "Token list limit reached"
            );
            userTokenList[msg.sender].push(token);
        }
        tokenBalances[msg.sender][token] += amount;

        emit DepositToken(msg.sender, token, amount);
    }

    function withdrawETH(
        address user,
        uint256 amount
    ) external onlyAuthorizedSpender nonReentrant {
        require(ethBalances[user] >= amount, "Insufficient ETH balance");
        ethBalances[user] -= amount;
        payable(msg.sender).transfer(amount);

        emit WithdrawETH(user, amount);
    }

    function withdrawToken(
        address user,
        address token,
        uint256 amount
    ) external onlyAuthorizedSpender nonReentrant {
        require(token != address(0), "Invalid token address");
        require(
            tokenBalances[user][token] >= amount,
            "Insufficient token balance"
        );

        tokenBalances[user][token] -= amount;
        IERC20(token).safeTransfer(msg.sender, amount);

        emit WithdrawToken(user, token, amount);
    }
}
